<?php

namespace labo\Bundle\TestmanuBundle\Entity;

use labo\Bundle\TestmanuBundle\Entity\laboBaseRepository;

/**
 * tauxTVARepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class tauxTVARepository extends laboBaseRepository {

	/**
	* defaultVal
	* Renvoie l'instance de la version par dÃ©faut (ou null)
	*/
	public function defaultVal() {
		$qb = $this->createQueryBuilder('element')
			->where('element.nom = :n')
			->setParameter('n', "normal");
		return $qb->getQuery()->getResult();
	}


}
